import Cookies from 'js-cookie';

export default {
    loginUser({ commit }, userData) {
        commit('setGreenhouseUser', userData);
        console.log('User data stored in Vuex after login:', userData);
        console.log('First Name:', userData.first_name);
        console.log('Last Name:', userData.last_name);
        console.log('Role:', userData.role);
    },
    async logoutUser({ commit }, router) { // Receive the router instance as the second argument
        try {
            const response = await fetch('http://192.168.1.228:5000/logout', {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json',
                },
                credentials: 'include',
            });

            if (response.ok) {
                console.log('Logout successful on the server (optional)');
            } else {
                console.error('Error during server-side logout (optional)');
            }
        } catch (error) {
            console.error('Error during logout API call:', error);
        } finally {
            Cookies.remove('jwt_token', { path: '/' });
            commit('clearGreenhouseUser');
            if (router) {
                console.log('Router instance received in action:', router);
                router.push('/login');
            } else {
                console.error('Router instance NOT received in action!');
            }
        }
    },
    async loadUserFromToken({ commit }) {
        console.log('loadUserFromToken action is executing!'); // Added console.log()
        const token = Cookies.get('jwt_token');
        if (token) {
            try {
                const response = await fetch('http://192.168.1.228:5000/verify-token', { // Replace with your actual endpoint
                    method: 'POST',
                    headers: {
                        'Content-Type': 'application/json',
                        'Authorization': `Bearer ${token}`, // Or however you send your token
                    },
                    credentials: 'include', // Add this line to include cookies
                });

                if (response.ok) {
                    const userData = await response.json();
                    commit('setGreenhouseUser', userData); // Update state with fetched data
                } else {
                    // Token is invalid or expired, clear it
                    Cookies.remove('jwt_token', { path: '/' });
                    commit('clearGreenhouseUser');
                }
            } catch (error) {
                console.error('Error verifying token:', error);
                // Handle network errors
                Cookies.remove('jwt_token', { path: '/' });
                commit('clearGreenhouseUser');
            }
        }
    },

};
