export default {
    loginUser({ commit }, userData) {
        commit('setGreenhouseUser', userData);

    },
     async logoutUser({ commit }) {
        try {
    //       // Optional: Call your Flask server's logout endpoint
            const response = await fetch('http://192.168.1.228:5000/logout', { 
                method: 'POST',
                headers: {
                'Content-Type': 'application/json',
                },
                credentials: 'include', // Crucial line for sending cookies
            });

          if (response.ok) {
            console.log('Logout successful on the server (optional)');
          } else {
            console.error('Error during server-side logout (optional)');
            // Handle potential errors from the server
          }
        } catch (error) {
          console.error('Error during logout API call:', error);
          // Handle network errors or other issues
        } finally {
          // Clear the JWT cookie
          Cookies.remove('jwt_token', { path: '/' }); // Replace 'your_jwt_cookie_name' with the actual name of your JWT cookie

          // Clear the user data in Vuex state
          commit('clearGreenhouseUser');

          // Redirect the user to the login page
          router.push('/login'); // Adjust the route as needed
        }
      },
};
